(function($){
  $(function(){
     	  $('#sup').click(function(event){
     	  	  $(this).next('ul').toggle();
     	  	  $('#aff').next('ul').hide();
     	  	  event.stopPropagation();
     	  });

     	  $('#aff').click(function(event){
     	  	  $(this).next('ul').toggle();
     	  	  $('#sup').next('ul').hide();
     	  	  event.stopPropagation();
     	  });

     	  $('html').click(function(){
     	  	  $('.header-info + ul').hide();
     	  });

     	  $('.nav-tabs li a').click(function(){
     	  	  $('.nav-tabs li a').removeClass('active');
     	  	  $(this).addClass('active');
     	  	  $('.tab-pane').hide();
     	  	  $('#'+$(this).data('target')).show();
     	  });

          $('.switch-offer-view-btn .grid-view-1').click(function(){
               $('.affoffer-result-list-1').hide();
               $('.affoffer-result-grid-1').show();

          });

          $('.switch-offer-view-btn .list-view-1').click(function(){
            $('.affoffer-result-grid-1').hide();
             $('.affoffer-result-list-1').show();
             
          });
          $('.switch-offer-view-btn .grid-view-2').click(function(){
               $('.affoffer-result-list-2').hide();
               $('.affoffer-result-grid-2').show();

          });

          $('.switch-offer-view-btn .list-view-2').click(function(){
            $('.affoffer-result-grid-2').hide();
             $('.affoffer-result-list-2').show();
             
          });
          
           $('.edit-column .pop-link').click(function(){
                $('.edit-column-panel').toggle();
           })
     })
})(jQuery);